import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import * as actions from "../actions/customer";
import { Grid, Paper, TableContainer, Table, TableHead, TableRow, TableCell, TableBody } from "@material-ui/core";
import CustomerForm from "./CustomerForm";


const Customers = (props) => {
    // const [x, setX] = useState(0)
    // setX(5)

    useEffect(() => {
        props.fetchAllCustomers()
    }, []) //componentDidMount

    return (
        <Paper>
            <Grid container>
                <Grid item xs={6}>
                    <CustomerForm />
                </Grid>
                <Grid item xs={6}>
                    <TableContainer>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Full Name</TableCell>
                                    <TableCell>Address</TableCell>
                                    <TableCell>Email</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {
                                    props.customerList.map((record, index) => {
                                        return (<TableRow key={index}>
                                            <TableCell>{record.fullName}</TableCell>
                                            <TableCell>{record.address}</TableCell>
                                            <TableCell>{record.email}</TableCell>
                                        </TableRow>)
                                    })
                                }
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Grid>
            </Grid>
        </Paper>
    );
}

const mapStateToProps = state => ({
    customerList: state.customer.list
})

const mapActionToProps = {
    fetchAllCustomers: actions.fetchAll
}

export default connect(mapStateToProps, mapActionToProps)(Customers);
